/**
 * Formats a time string (HH:mm:ss) to 12-hour format with AM/PM in the user's local timezone.
 * Assumes the input time is in UTC.
 * 
 * @param {string} timeString - The time string (e.g., "14:30:00")
 * @param {string} dateString - The date string (e.g., "2023-11-27") associated with the time.
 * @returns {string} - Formatted local time (e.g., "08:00 PM")
 */
export const formatTime = (timeString, dateString) => {
    if (!timeString) return "--:--";

    // If no date provided, assume today (careful with this assumption across midnight)
    // Better to require dateString for accuracy.
    const dateBase = dateString || new Date().toISOString().split('T')[0];

    // Construct UTC ISO string: "YYYY-MM-DDTHH:mm:ssZ"
    const utcDateTimeString = `${dateBase}T${timeString}Z`;

    try {
        const date = new Date(utcDateTimeString);

        // Check if date is valid
        if (isNaN(date.getTime())) return timeString;

        return date.toLocaleTimeString("en-US", {
            hour: "2-digit",
            minute: "2-digit",
            hour12: true
        });
    } catch (e) {
        console.error("Time formatting error:", e);
        return timeString;
    }
};

/**
 * Formats a date string (YYYY-MM-DD) to a readable format.
 * Example: "2023-10-27" -> "Oct 27, 2023"
 * @param {string} dateString 
 * @returns {string}
 */
export const formatDate = (dateString) => {
    if (!dateString) return "N/A";
    return new Date(dateString).toLocaleDateString("en-US", {
        year: "numeric",
        month: "short",
        day: "numeric"
    });
};
