import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api/axios";

export default function Dashboard() {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);
    const [attendance, setAttendance] = useState(null);
    const [announcements, setAnnouncements] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem("token");
        if (!token) {
            navigate("/login");
            return;
        }

        let isMounted = true;
        setLoading(true);
        setError(null);

        // Fetch logged-in user first, then attendance and announcements
        api.get("/user")
            .then((res) => {
                if (!isMounted) return;
                // Laravel responses sometimes wrap payload; try common shapes
                const payload = res.data && (res.data.user || res.data.data || res.data);
                setUser(payload);
                // Fetch attendance and announcements in parallel
                return Promise.all([api.get("/my-attendance"), api.get("/announcements")]);
            })
            .then(([attRes, annRes]) => {
                if (!isMounted) return;
                const attPayload = attRes?.data?.data ?? attRes?.data ?? null;
                const annPayload = annRes?.data?.data ?? annRes?.data ?? [];
                setAttendance(attPayload);
                setAnnouncements(Array.isArray(annPayload) ? annPayload.slice(0, 3) : (annPayload.data ?? []).slice(0,3));
            })
            .catch((err) => {
                if (!isMounted) return;
                console.error("Dashboard data error:", err);
                setError(err?.response?.data?.message || err.message || "Failed to load data");
            })
            .finally(() => {
                if (!isMounted) return;
                setLoading(false);
            });

        return () => {
            isMounted = false;
        };
    }, [navigate]);

    if (loading) {
        return (
            <div style={{ padding: 20 }}>
                <h2>Dashboard</h2>
                <p>Loading...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div style={{ padding: 20 }}>
                <h2>Dashboard</h2>
                <p style={{ color: "red" }}>Error: {error}</p>
            </div>
        );
    }

    return (
        <div style={{ padding: 20 }}>
            <h2>Dashboard</h2>

            <section style={{ marginBottom: 20 }}>
                <h3>
                    Welcome, {user?.name ?? "User"}
                </h3>
                <p>Email: {user?.email ?? "N/A"}</p>
                <p>Role ID: {user?.role_id ?? "N/A"}</p>
            </section>

            <section style={{ marginBottom: 20 }}>
                <h4>Today's Attendance</h4>
                {attendance ? (
                    <div>
                        <p>Check-in: {attendance.check_in ?? "Not available"}</p>
                        <p>Check-out: {attendance.check_out ?? "Not available"}</p>
                        <p>Status: {attendance.status ?? "Not available"}</p>
                    </div>
                ) : (
                    <p>No attendance record for today.</p>
                )}
            </section>

            <section>
                <h4>Latest Announcements</h4>
                {announcements && announcements.length > 0 ? (
                    <ul>
                        {announcements.slice(0, 3).map((ann, idx) => (
                            <li key={ann.id ?? idx} style={{ marginBottom: 10 }}>
                                <strong>{ann.title ?? ann.name ?? `Announcement ${idx + 1}`}</strong>
                                <div>{ann.body ?? ann.description ?? ann.content ?? ""}</div>
                                {ann.created_at && <small>{ann.created_at}</small>}
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No announcements.</p>
                )}
            </section>
        </div>
    );
}
